query {
  products(
    first: 10,
    channel: "online-inr",
    filter: {
      attributes: [
        {
          slug: "color"
          values: ["white-gold"]
        }
      ]
    }
  ) {
    edges {
      node {
        id
        name
        variants {
          id
          name
          sku
        }
        media{
            url
        }
      }
    }
  }
}

query GetProduct($id: ID, $slug: String, $channel: String) {
  product(id: $id, slug: $slug, channel: $channel) {
    id
    name
    slug
    description
    seoTitle
    seoDescription
    defaultVariant {
      id
      name
      sku
      pricing {
        priceUndiscounted {
          gross {
            amount
            currency
          }
        }
        price {
          gross {
            amount
            currency
          }
        }
      }
    }
    variants {
      id
      name
      sku
      pricing {
        price {
          gross {
            amount
            currency
          }
        }
      }
      attributes {
        attribute {
          id
          name
        }
        values {
          id
          name
        }
      }
    }
    media {
      id
      url
      alt
      type
    }
    attributes {
      attribute {
        id
        name
      }
      values {
        id
        name
      }
    }
    category {
      id
      name
      slug
    }
    productType {
      id
      name
    }
    isAvailableForPurchase
    availableForPurchase
  }
}

mutation AddToCart($input: CheckoutCreateInput!) {
  checkoutCreate(input: $input) {
    checkout {
      id
      token
      lines {
        id
        quantity
        variant {
          id
          name
          product {
            id
            name
            slug
            thumbnail {
              url
            }
          }
          pricing {
            price {
              gross {
                amount
                currency
              }
            }
          }
        }
      }
    }
    errors {
      field
      message
    }
  }
}

query GetShippingZones {
  shippingZones(first: 10) {
    edges {
      node {
        id
        name
        countries {
          code
          country
        }
        shippingMethods {
          id
          name
          minimumOrderPrice {
            amount
            currency
          }
          type
        }
      }
    }
  }
}

mutation SetShippingAddress($checkoutId: ID!, $shippingAddress: AddressInput!) {
  checkoutShippingAddressUpdate(checkoutId: $checkoutId, shippingAddress: $shippingAddress) {
    checkout {
      id
      shippingAddress {
        id
        firstName
        lastName
        streetAddress1
        city
        countryArea
        postalCode
        country {
          code
        }
      }
    }
    errors {
      field
      message
    }
  }
}

mutation SetBillingAddress($checkoutId: ID!, $billingAddress: AddressInput!) {
  checkoutBillingAddressUpdate(checkoutId: $checkoutId, billingAddress: $billingAddress) {
    checkout {
      id
      billingAddress {
        id
        firstName
        lastName
        streetAddress1
        city
        countryArea
        postalCode
        country {
          code
        }
      }
    }
    errors {
      field
      message
    }
  }
}

query GetAvailableShippingMethods($checkoutId: ID!) {
  checkout(id: $checkoutId) {
    id
    availableShippingMethods {
      id
      name
      price {
        amount
        currency
      }
      minimumOrderPrice {
        amount
        currency
      }
      type
    }
    shippingAddress {
      country {
        code
        country
      }
      city
      postalCode
    }
  }
}

query GetShippingMethods($checkoutId: ID!) {
  checkout(id: $checkoutId) {
    id
    availableShippingMethods {
      id
      name
      price {
        amount
        currency
      }
      minimumOrderPrice {
        amount
        currency
      }
    }
      __typename
  }
}

mutation SetShippingMethod($checkoutId: ID!, $shippingMethodId: ID!) {
  checkoutShippingMethodUpdate(checkoutId: $checkoutId, shippingMethodId: $shippingMethodId) {
    checkout {
      id
      shippingMethod {
        id
        name
        price {
          amount
          currency
        }
      }
    }
    errors {
      field
      message
    }
  }
}

query GetCheckoutTotal($checkoutId: ID!) {
  checkout(id: $checkoutId) {
    id
    totalPrice {
      gross {
        amount
        currency
      }
    }
    subtotalPrice {
      gross {
        amount
        currency
      }
    }
    shippingPrice {
      gross {
        amount
        currency
      }
    }
  }
}

mutation AddPaymentMethod($checkoutId: ID!, $paymentMethod: PaymentInput!) {
  checkoutPaymentCreate(checkoutId: $checkoutId, input: $paymentMethod) {
    checkout {
      id
      totalPrice {
        gross {
          amount
          currency
        }
      }
    }
    errors {
      field
      message
    }
  }
}

