graphql.query:
mutation CompleteCheckout($checkoutId: ID!) {
  checkoutComplete(checkoutId: $checkoutId) {
    order {
      id
      status
      number
      total {
        gross {
          amount
          currency
        }
      }
      lines {
        id
        quantity
        variant {
          id
          name
          product {
            name
          }
        }
      }
    }
    confirmationNeeded
    confirmationData
    errors {
      field
      message
    }
  }
}
